import style from './Product.module.css';
const ProductItem = ({title,desc,image,price}) => {
    const imageurl = image
    console.log(`imageurl: ${imageurl}`);
    return (
        <div className={style['product-card']}>
            <div style={{backgroundImage:`url(/product-images/product-1)`}} className={style['prd-img']}></div>
            
            <div className={style['prd-info']}>
            <p  className={style['prd-title']}>{title}</p>
                <p className={style['prd-description']}>{desc}</p>
                <div className={style['prd-buy']}>
                    <p className={style['prd-price']}>{price}</p>
                    <div className={style['prd-cart']}></div>
                </div>
            </div>
        </div>
    )
}
export default ProductItem;
