import style from './Product.module.css';
import ProductItem from './ProductItem';
import React, { Component } from 'react';
import { useNavigate } from 'react-router-dom';


const navigate = useNavigate();

export class Product extends Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [
                {
                    id: "1",
                    img: "product-1.png",
                    title: "Slimming Gel Body",
                    desc: "Крем ночной",
                    price: 69.5,
                },
                {
                    id: "2",
                    img: "product-2.png",
                    title: "Slimm Gel ",
                    desc: "Крем дневной",
                    price: 49,

                },
                {
                    id: "3",
                    img: "product-3.png",
                    title: "Slimming Gel ",
                    desc: "Масло для тела суперуход",
                    price: 39,

                },
                {
                    id: "4",
                    img: "product-4.png",
                    title: "Gel ",
                    desc: "Гель для душа",
                    price: 29.5,

                },
                {
                    id: "5",
                    img: "product-5.png",
                    title: "Slimming Gel ",
                    desc: "Лосьон для тела",
                    price: 18.5,
                },
                {
                    id: "6",
                    img: "product-6.png",
                    title: "Slimming Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 24.5,
                },
                {
                    id: "7",
                    img: "product-7.png",
                    title: "Oil",
                    desc: "Масло для лица",
                    price: 19.5,
                },
                {
                    id: "8",
                    img: "product-8.png",
                    title: "Пенка",
                    desc: "Пенка для умывания",
                    price: 18.5,
                },
                {
                    id: "9",
                    img: "product-9.png",
                    title: "Сыворотка ",
                    desc: "Сыворотка для лица для домашнего ухода",
                    price: 22.5,
                }
            ],
            activeCart: {}
        }
        this.addToCart = this.addToCart.bind(this)
        this.getAmount = this.getAmount.bind(this)
        this.getSum = this.getSum.bind(this)
        this.deleteItem = this.deleteItem.bind(this)
    }
    render() {
        startFunc = () => {
            
            navigate("/")  
        }
        return (
            <div className={style['catalog']}>
                <p className={style['news']}>Новинки</p>
                <div className={style['catalog-grid']}>
                    {this.props.items.map(el => (<ProductItem key={el.id} item={el} onAdd={this.props.onAdd}/>))}
                </div>
                <div className={style['back-btn']} onClick={startFunc()}><p>На главную</p></div>
            </div>
        )
    }

}
export default Product;
