import style from './Product.module.css';
import { useNavigate } from 'react-router-dom';
import ProductItem from './ProductItem';
import productBase from '../../context/Products';
import activeCart from '../../../src/context/activeCart'
import React from 'react';
const Product = () => {

        return (
            <div className={style['catalog']}>
             <p className={style['news']}>Новинки</p>
             <div className={style['catalog-grid']}>
             {productBase.map((el, index) => <ProductItem key={index} title={el.title} desc={el.desc} image={el.imagе} price={el.price} />)}
             </div>
               <div className={style['back-btn']}><p>На главную</p></div>
            </div>
        )
}
export default Product;
