import style from './Product.module.css';
import ProductItem from './ProductItem';
import React, { Component } from 'react';
import CatalogPage from '../../pages/CatalogPage/CatalogPage';



export class Product extends Component {

constructor(props) {
    super(props) 
        this.state ={
            navigate: false
        }
    }
    handleClick = () => {
        this.setState({navigate: true})
    }

    render() {
       

        return (
            <div className={style['catalog']}>
                <p className={style['news']}>Новинки</p>
                <div className={style['catalog-grid']}>
                    {this.props.items.map(el => (<ProductItem key={el.id} item={el} onAdd={this.props.onAdd}/>))}
                </div>
                <div className={style['back-btn']} onClick={this.handleClick}><p>На главную</p></div>
                {th}
            </div>
        )
    }

}
export default Product;
