import style from './Cart.module.css';
import CartItem from './CartItem';


const Cart = (props) => {   
    return (
        <div className={style['cart']}>
            <p className={style['cart-title']}>Корзина</p>
            <div className={style['cart-form']}>
            {props.activeCart.map(el =>( <CartItem key={el.id} item={el} amount={props.getAmount(el.id)} delete={props.deleteItem} />))}
                <div className={style['cart-product']}></div>
                <div className={style['cart-amount']}></div>
                <div className={style['cart-total']}>{props.getSum()}</div>
            </div>
        </div>
        )
}

export default Cart;
