import React, {Component} from 'react';
import style from './Product.module.css';


export class ProductItem extends Component {

render() {
    return (
        <div className={style['product-card']}>
            <div style={{backgroundImage:`url(${this.items.image})`}} className={style['prd-img']}></div>
            
            <div className={style['prd-info']}>
            <p  className={style['prd-title']}>{this.items.title}</p>
                <p className={style['prd-description']}>{this.items.desc}</p>
                <div className={style['prd-buy']}>
                    <p className={style['prd-price']}>{this.items.price} BYN</p>
                    <div className={style['prd-cart']}></div>
                </div>
            </div>
        </div>
    )
}
}
export default ProductItem;
