import style from './CatalogPage.module.css';
import Header from '../../components/Header/Header';
import Cart from '../../components/Cart/Cart';
import React, { Component, useState } from 'react';
import Product from '../../components/Product/Product';




class CatalogPage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [
                {
                    id: "1",
                    img: "product-1.png",
                    title: "Slimming Gel Body",
                    desc: "Гель для тела для похудения",
                    price: 69.9,
                },
                {
                    id: "2",
                    img: "product-2.png",
                    title: "Slimm Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 49.9,

                },
                {
                    id: "3",
                    img: "product-3.png",
                    title: "Slimming Gel ",
                    desc: "Масло для тела",
                    price: 39.9,

                },
                {
                    id: "4",
                    img: "product-4.png",
                    title: "Gel ",
                    desc: "Гель для душа для похудения",
                    price: 29.9,

                },
                {
                    id: "5",
                    img: "product-5.png",
                    title: "Slimming Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 18.9,
                },
                {
                    id: "6",
                    img: "product-6.png",
                    title: "Slimming Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 24.9,
                }
            ],
            activeCart: {
                // id: { amount }
            }
        }
        this.addToCart = this.addToCart.bind(this)
    }


    render() {
        return (
            <div className={style['catalog-page']}>
                <Header />
                <div className={style['main']}>
                    <Product items={this.state.items} onAdd={this.addToCart} />
                    <Cart activeCart={this.getCards()}  />
                </div>
            </div>
        );
    }


    addToCart(item) {
        this.setState((previousState) => {
            const isItemInCart = previousState.activeCart.hasOwnProperty(item.id);


                const incrementedAmount = isItemInCart ? previousState.activeCart[item.id].amount + 1 : 1;
                return {
                    ...previousState,
                    activeCart: {
                        ...previousState.activeCart,
                        [item.id]: { amount: incrementedAmount }
                    }
                };
        });


    };

    getCards() {
        return this.state.items.filter(item => this.state.activeCart.hasOwnProperty(item.id))
    }
}
export default CatalogPage;
