import style from './CatalogPage.module.css';
import Header from '../../components/Header/Header';
import Cart from '../../components/Cart/Cart';
import React, { Component, useState } from 'react';
import Product from '../../components/Product/Product';




class CatalogPage extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            items: [
                {
                    id: "1",
                    img: "product-1.png",
                    title: "Slimming Gel Body",
                    desc: "Гель для тела для похудения",
                    price: 69.5,
                },
                {
                    id: "2",
                    img: "product-2.png",
                    title: "Slimm Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 49,

                },
                {
                    id: "3",
                    img: "product-3.png",
                    title: "Slimming Gel ",
                    desc: "Масло для тела",
                    price: 39,

                },
                {
                    id: "4",
                    img: "product-4.png",
                    title: "Gel ",
                    desc: "Гель для душа для похудения",
                    price: 29.5,

                },
                {
                    id: "5",
                    img: "product-5.png",
                    title: "Slimming Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 18.5,
                },
                {
                    id: "6",
                    img: "product-6.png",
                    title: "Slimming Gel ",
                    desc: "Лосьон для тела для похудения",
                    price: 24.5,
                }
            ],
            activeCart: {}
        }
        this.addToCart = this.addToCart.bind(this)
        this.getAmount = this.getAmount.bind(this)
        this.getSum = this.getSum.bind(this)
        this.deleteItem = this.deleteItem.bind(this)
    }


    render() {
        return (
            <div className={style['catalog-page']}>
                <Header />
                <div className={style['main']}>
                    <Product items={this.state.items} onAdd={this.addToCart} />
                    <Cart activeCart={this.getCards()} getAmount={this.getAmount}   getSum={this.getSum}/>
                </div>
            </div>
        );
    }


    addToCart(item) {
        this.setState((previousState) => {
            const isItemInCart = previousState.activeCart.hasOwnProperty(item.id);


                const incrementedAmount = isItemInCart ? previousState.activeCart[item.id].amount + 1 : 1;
                return {
                    ...previousState,
                    activeCart: {
                        ...previousState.activeCart,
                        [item.id]: { amount: incrementedAmount }
                    }
                };
        });


    };

    getCards() {
        return this.state.items.filter(item => this.state.activeCart.hasOwnProperty(item.id))
    }

    getAmount(id) {
        return this.state.activeCart[id].amount;
    }

    getSum() {
        let sum = 0
        this.getCards().map(el => {
            sum += el.price * this.getAmount(el.id)
        })

        return sum
    }

    deleteItem (id) {
        this.setState((previousState) => {
            const isItemInCart = previousState.activeCart.hasOwnProperty(id);
            const amountBolsheEdenitse = previousState.activeCart[id].amount > 1 ? true : false;

                const incrementedAmount = amountBolsheEdenitse ? previousState.activeCart[id].amount - 1 : delete previousState.activeCart[id];

                


                return {
                    ...previousState,
                    activeCart: {
                        ...previousState.activeCart,
                        [id]: { amount: incrementedAmount }
                    }
                };
        });
    }
}
export default CatalogPage;
